import React, { useState, useEffect } from 'react';
import NavBar from './components/NavBar';
import CreateItem from './components/CreateItem';
import ListItems from './components/ListItems';

export default function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchItems = () => {
    setLoading(true);
    setError('');
    fetch('/api/items')
      .then((r) => r.json())
      .then((data) => {
        setItems(data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load items');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleCreate = (text) => {
    setError('');
    fetch('/api/items', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text })
    })
      .then((r) => r.json())
      .then((item) => {
        setItems((prev) => [...prev, item]);
      })
      .catch(() => setError('Failed to create item'));
  };

  const handleUpdate = (id, text) => {
    setError('');
    fetch(`/api/items/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text })
    })
      .then((r) => r.json())
      .then((updated) => {
        setItems((prev) => prev.map((it) => (it.id === id ? updated : it)));
      })
      .catch(() => setError('Failed to update item'));
  };

  const handleDelete = (id) => {
    setError('');
    fetch(`/api/items/${id}`, { method: 'DELETE' })
      .then(() => {
        setItems((prev) => prev.filter((it) => it.id !== id));
      })
      .catch(() => setError('Failed to delete item'));
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <NavBar />
      <main className="max-w-3xl mx-auto p-4 space-y-4">
        {error && <div className="bg-red-100 text-red-700 p-2 rounded text-sm">{error}</div>}
        <CreateItem onCreate={handleCreate} />
        <ListItems items={items} loading={loading} onRefresh={fetchItems} onUpdate={handleUpdate} onDelete={handleDelete} />
      </main>
    </div>
  );
}
