import React, { useState, useEffect } from 'react';
import NavBar from './components/NavBar';
import CreateItem from './components/CreateItem';
import ListItems from './components/ListItems';
import { getItems, createItem, updateItem, deleteItem } from './utils/api';

// Root application component that wires together navigation and item flows.
export default function App() {
  const [items, setItems] = useState([]); // List of items loaded from the API
  const [loading, setLoading] = useState(false); // Whether a list request is in progress
  const [error, setError] = useState(''); // User-friendly error message

  // Load all items from the backend API.
  const fetchItems = async () => {
    setLoading(true);
    setError('');
    try {
      const data = await getItems();
      setItems(data);
    } catch (e) {
      setError(e.message || 'Failed to load items');
    } finally {
      setLoading(false);
    }
  };

  // Fetch items when the component mounts.
  useEffect(() => {
    fetchItems();
  }, []);

  // Handle creating a new item.
  const handleCreate = async (text) => {
    setError('');
    try {
      const item = await createItem(text);
      setItems((prev) => [...prev, item]);
    } catch (e) {
      setError(e.message || 'Failed to create item');
    }
  };

  // Handle updating an existing item.
  const handleUpdate = async (id, text) => {
    setError('');
    try {
      const updated = await updateItem(id, text);
      setItems((prev) => prev.map((it) => (it.id === id ? updated : it)));
    } catch (e) {
      setError(e.message || 'Failed to update item');
    }
  };

  // Handle deleting an item.
  const handleDelete = async (id) => {
    setError('');
    try {
      await deleteItem(id);
      setItems((prev) => prev.filter((it) => it.id !== id));
    } catch (e) {
      setError(e.message || 'Failed to delete item');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <NavBar />
      <main className="max-w-3xl mx-auto p-4 space-y-4">
        {error && <div className="bg-red-100 text-red-700 p-2 rounded text-sm">{error}</div>}
        <CreateItem onCreate={handleCreate} />
        <ListItems
          items={items}
          loading={loading}
          onRefresh={fetchItems}
          onUpdate={handleUpdate}
          onDelete={handleDelete}
        />
      </main>
    </div>
  );
}
